;prompt  equ 0F86Ch
prompt  equ 0F89Dh
puts    equ 0F818h

	org 0
;	lxi h, msg
;	call puts
;930h 
;76D0-7FFF
;1000h
	;call config_video
	;call P_VG75
	call monitor_config
	lxi b, 8E8h ;размер
	lxi d, msg
	lxi h, 76D0h
	call memcpy
	jmp prompt



	; bc: number of bytes to copy
	; de: source block
	; hl: target block
memcpy:
	mov     a,b         ;Copy register B to register A
	ora     c           ;Bitwise OR of A and C into register A
	rz                  ;Return if the zero-flag is set high.
loop:
	ldax    d          ;Load A from the address pointed by DE
	mov     m,a         ;Store A into the address pointed by HL
	inx     d          ;Increment DE
	inx     h          ;Increment HL
	dcx     b          ;Decrement BC   (does not affect Flags)
	mov     a,b         ;Copy B to A    (so as to compare BC with zero)
	ora     c           ;A = A | C      (set zero)
	jnz     loop        ;Jump to 'loop:' if the zero-flag is not set.
	ret                 ;Return

monitor_config:
	lxi h, 0d001h
	mvi m, 00h
	dcx h
	mvi m, 4dh
	mvi m, 1dh
	mvi m, 99h;mvi m, 77h;
	;mvi m, 93h; мерцающее подчёркивание
	mvi m, 0b3h; немерцающее подчёркивание
	;mvi m, 0a3; немерцающий негативный видеоблок
;ПДП
	mvi a, 80h
	sta 0f808h; запись управляющего слова в регистр управления
	lxi h, 0f804h
	mvi m, 0d0h; мл. адрес откуда
	mvi m, 76h; ст. адрес откуда
	inx h; 0f805h
	mvi m, 23h
	mvi m, 49h ; размер массива 0923h и единица в разряде D14 - направление передачи
;ПДП end
	lxi h, 0d001h
	mvi m, 27h
	mvi m, 0e0h
;ПДП
	mvi a, 0a4h ;автозагрузка, удлинённая запись, канал 2
	sta 0f808h    
;ПДП end
	ret

config_video:
	lxi h, 0d001h
	mvi m, 00h
	dcx h
	mvi m, 4dh
	mvi m, 1dh
	mvi m, 77h
	mvi m, 0b3h ;93h
	mvi a, 80h
;	sta 0f808h
;	lxi h, 0f804h
;	mvi m, 0d0h
;	mvi m, 86h
;	inx h
;	mvi m, 23h
;	mvi m, 49h
	lxi h, 0d001h
	mvi m, 27h
	mvi m, 0e0h
;	mvi a, 0a4h
;	sta 0f808h
	ret

P_VG75: 
	lxi h, 0d001h ; команда формат экрана
	mvi m, 00h
	dcx h
	mvi m, 4dh	; 77 --> т.е 78 колонок
	mvi m, 25h	; 37+1 строк (т.е всего 39 строк)
	mvi m, 77h	; 8 линий в знакоместе, 8 подчёркив-е
	mvi m, 53h	; 0101.0011
	mvi m, 27h	; команда начало отображения
	mov a, m
;wait:
;	mov a,m
;	ani 20h
;	jz wait
	mvi a, 80h
	sta 0f808h	; 0f800 - адрес ПДП ВТ57
	mvi a, 0D0h
	sta 0f804h	; начальный адрес экрана 74D0H
	mvi a, 076h
	sta 0f804h	; начальный адрес экрана 74D0H
	mvi a, 093h
	sta 0f805h	; режим
	mvi a, 04bh
	sta 0f805h	; режим
	mvi a, 0a4h
	sta 0f808h
	ret	


